package com.lrl.liustationspring;

import org.junit.Test;

import java.io.*;
import java.util.Base64;


public class ImageIO {
    @Test
    public static void main(String[] args) {
        String input = "data:image/jpeg;base64,";
        String dir = "/imgout";
        File file = new File(dir+"img"+(int)(Math.random()*5)+".jpeg");

        System.out.println(file.getAbsolutePath());
        String[] imgStructure = input.split(",");
        byte[] decodedImg = Base64.getDecoder().decode(imgStructure[1]);

        try {
            FileOutputStream out = new FileOutputStream(file);
            out.write(decodedImg);
            out.close();

        } catch (FileNotFoundException e) {
            throw new RuntimeException(e);
        } catch (IOException e) {
            throw new RuntimeException(e);
        }

    }

    public String saveImage(String base64Img){
        String dir = "/imgout";
        File file = new File(dir+"img"+(int)(Math.random()*5)+".jpeg");

        System.out.println(file.getAbsolutePath());
        String[] imgStructure = base64Img.split(",");
        byte[] decodedImg = Base64.getDecoder().decode(imgStructure[1]);

        try {
            FileOutputStream out = new FileOutputStream(file);
            out.write(decodedImg);
            out.close();

        } catch (FileNotFoundException e) {
            throw new RuntimeException(e);
        } catch (IOException e) {
            throw new RuntimeException(e);
        }

        return file.getAbsolutePath();
    }
}
